import React, { useState } from "react";
import {
    useRecoilValue,
  } from 'recoil';
import TodoItemCreator from 'functions/TodoItemCreator';
import TodoItem from 'recoilStates/TodoItem';
import todoListState from 'components/atom/todoListState';
import Dropdown from 'react-dropdown';

const AddRoad = () => {
    const todoList = useRecoilValue(todoListState);
    const [state, setState] = useState('');
    const options = [
      'one', 'two', 'three'
    ];

    const onSelect = (event) => {
      console.log(event);
      setState({ value: event.value });
    };


    return (
      <>
        <h3>AddRoad Page</h3>
          <TodoItemCreator />

          {todoList.map((todoItem) => (
            <TodoItem key={todoItem.id} item={todoItem} />
          ))}
          <Dropdown options={options} onChange={onSelect} value={state} placeholder="Select an option" />
      </>
    );
};

export default AddRoad;
