import React from "react";
import AboutMain from 'components/organisms/AboutMain';
import 'components/styles/AboutNav.css';
import { Link } from 'react-router-dom';

const About = () => {
  if (window.location.hash === '#/about-luminouss/main' || window.location.hash === '#/about-luminouss'){
    document.getElementById("vision-mission").style.color = 'green';
    document.getElementById("team").style.color = 'black';
    document.getElementById("solution").style.color = 'black';
  } else if (window.location.hash === '#/about-luminouss/team') {
    document.getElementById("vision-mission").style.color = 'black';
    document.getElementById("team").style.color = 'green';
    document.getElementById("solution").style.color = 'green';
  } else if (window.location.hash === '#/about-luminouss/solution') {
    document.getElementById("vision-mission").style.color = 'black';
    document.getElementById("solution").style.color = 'green';
    document.getElementById("team").style.color = 'black';
  }
  return (
    <nav>
       <ul className="about-ul">
         <li className='list'>
          <Link to='/about-luminouss/main' id="vision-mission"> {'Vision&Mission'} </Link>
         </li>
         <li className='list'>
          <Link to='/about-luminouss/team' id="team"> Team </Link>
         </li>
         <li className='list'>
          <Link to='/about-luminouss/solution' id="solution"> Solution </Link>
         </li>
       </ul>
       <AboutMain />
    </nav>
  );
}

export default About;
