import React, { useState } from "react";
import firebase from "global/fbase";
import { useHistory, Link } from "react-router-dom";
import KakaoLogin from 'react-kakao-login';
import KakaoAuthKey from 'global/KakaoAuthKey';

const SignIn = () => {
    const history = useHistory();
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");

    const onChange = (event) => { 
        const {
            target: {
                name, value
            }
        } = event;
        if (name === "email") {
            setEmail(value);
        } else if (name === "password") {
            setPassword(value);
        }
    };

    const onSubmit = async (event) => {
        event.preventDefault();
        try {
            // let data;
            await firebase.auth().signInWithEmailAndPassword(email, password);
            //어케 홈 화면으로 다시 돌려보내지
            history.push("/");
        } catch (error) {
            setError(error.message);
            console.log(error.message);
        }
    };
    const KaKaoBtn = styled(KaKaoLogin)`
        padding: 0;
        width: 300px;
        height: 45px;
        line-height: 44px;
        color: #783c00;
        background-color: #ffeb00;
        border: 1px solid transparent;
        border-radius: 3px;
        font-size: 14px;
        font-weight: bold;
        text-align: center;
        cursor: pointer;
        &:hover {
            box-shadow: 0 0px 15px 0 rgba(0, 0, 0, 0.2);
        }
    `;



    return (
    <div style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems:'center',
        flexDirection: 'column'
    }}>
        <h2>로그인하고 새로운 산책로를 추가해보세요</h2>
        <form onSubmit={onSubmit} style={{
            display: 'flex',
            justifyContent: 'center',
            alignItems:'center',
            flexDirection: 'column'
        }}>
            <h4>이메일</h4>
            <input name="email" type="email" value={email} onChange={onChange} required></input>
            <h4>비밀번호</h4>
            <input name="password" type="password" value={password} onChange={onChange} required></input>
            <input type="submit" value="로그인" style={{
                marginTop: '20px',
                width: '200px',
                height: '30px'
            }}></input>
            {error}
        </form>
        <KaKaoBtn jsKey={KakaoAuthKey} onSuccess={console.log} onFail={console.error} onLogout={console.info} getProfile={true} />        
        <h3>아직 계정이 없으신가요?</h3> <Link to="/sign-up">회원가입하기</Link>
    </div>
    );
};

export default SignIn;
