import React, { useEffect, useState } from "react";
import { Link, useHistory } from "react-router-dom";
import "components/styles/Home.css"
import firebase from "global/fbase";
import FirestoreData from 'components/molecules/FirestoreData';

const Home = ({ isLoggedIn }) => {
    const history = useHistory();
    const [nameArray, setNameArray] = useState([]);
    //login 안 된 경우, 로그인 필요합니다 알러트 띄우기
    const onClick = (event) => {
        event.preventDefault();
        if (isLoggedIn){
            return;
        } else {
            window.alert("Login required");
            history.push('sign-in');
            // return (<Route path="/sign-in" component={SignIn} />);
        }
    };

    useEffect((  ) => {
        firebase.firestore().collection('WalkRoad')
        .get()
        .then((res) => {
            res.forEach((doc) => {
                setNameArray(doc.data().roadName);
                // console.log(doc.id, "=", doc.data().roadName);
            });
        })
        .catch((error) => {
            console.log(error);
        })
    }, [nameArray])
 
    return (
        <>
            <div className="mainContainer">
                <h1 className="mainTitle">
                    시각장애인의 눈과 발이 되어주세요.
                </h1>
                <h3 className="explanation">
                    문제 설명...
                </h3>

                <div className="linkBox">
                    <div className="addWalkway_text">산책로 정보 추가하기</div>
                    <Link to="/add-road-info" className="addWalkway" onClick={onClick}>
                        +
                    </Link>
                </div>
                {nameArray.map((roadName) => <FirestoreData key={roadName.id} item={roadName} />)}
            </div>
        </>
    );
};

export default Home;
