import React, { useEffect } from "react";
import AboutMain from 'components/organisms/AboutMain';
import 'components/styles/AboutNav.css';
import { Link } from 'react-router-dom';

const About = () => {
  var vision = document.getElementById('vision');
  var team = document.getElementById('team');
  var solution = document.getElementById('solution');

  useEffect(() => {
    if (window.location.hash === '#/about-luminouss/main' || window.location.hash === '#/about-luminouss'){
      vision.style.color = 'green';
      team.style.color = 'black';
      solution.style.color = 'black';
    } else if (window.location.hash === '#/about-luminouss/team') {
      vision.style.color = 'black';
      team.style.color = 'green';
      solution.style.color = 'green';
    } else if (window.location.hash === '#/about-luminouss/solution') {
      vision.style.color = 'black';
      team.style.color = 'green';
      solution.style.color = 'black';
    }
  }, [vision.style, team.style, solution.style])

  return (
    <nav>
       <ul className="about-ul">
         <li className='list'>
          <Link to='/about-luminouss/main' id="vision"> Vision&Mission </Link>
         </li>
         <li className='list'>
          <Link to='/about-luminouss/team' id="team"> Team </Link>
         </li>
         <li className='list'>
          <Link to='/about-luminouss/solution' id="solution"> Solution </Link>
         </li>
       </ul>
       <AboutMain />
    </nav>
  );
}

export default About;
