import {useEffect, useRef} from 'react';

const useTestFadeIn = () => {
    const element = useRef();
    useEffect(() => {
        if (element.current) {
            const {current} = element;
            current.style.transition = `opacity 2s`;
            current.style.opacity = 1;
        }
    }, []);
    return {ref: element, style:{opacity:0, display:"block",
    backgroundColor: 'black',
    width:window.innerWidth,
    height:window.innerHeight, zIndex:'2' } };
};
export default useTestFadeIn;
