import React, { useState } from "react";
import firebase from "global/fbase";
import { useHistory, Link } from "react-router-dom";
import KakaoLogin from 'react-kakao-login';
import KakaoAuthKey from 'global/KakaoAuthKey';

const {Kakao} = window;

const SignIn = () => {
    const history = useHistory();
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");

    const onChange = (event) => { 
        const {
            target: {
                name, value
            }
        } = event;
        if (name === "email") {
            setEmail(value);
        } else if (name === "password") {
            setPassword(value);
        }
    };

    const onSubmit = async (event) => {
        event.preventDefault();
        try {
            // let data;
            await firebase.auth().signInWithEmailAndPassword(email, password);
            //어케 홈 화면으로 다시 돌려보내지
            history.push("/");
        } catch (error) {
            setError(error.message);
            console.log(error.message);
        }
    };
    const loginWithKaKao = () => {
        try {
            return new Promise((resolve, reject) => {
                if (!Kakao){
                    reject('No KaKao instance');
                }
                Kakao.Auth.login({
                    success: function(response) {
                      console.log(response);
                    },
                    fail: function(error) {
                      console.log(error);
                    },
                  });

            })
        } catch (err) {
            console.error(err);
        }
    };




    return (
    <div style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems:'center',
        flexDirection: 'column'
    }}>
        <h2>로그인하고 새로운 산책로를 추가해보세요</h2>
        <form onSubmit={onSubmit} style={{
            display: 'flex',
            justifyContent: 'center',
            alignItems:'center',
            flexDirection: 'column'
        }}>
            <h4>이메일</h4>
            <input name="email" type="email" value={email} onChange={onChange} required></input>
            <h4>비밀번호</h4>
            <input name="password" type="password" value={password} onChange={onChange} required></input>
            <input type="submit" value="로그인" style={{
                marginTop: '20px',
                width: '200px',
                height: '30px'
            }}></input>
            {error}
        </form>
        <button onClick={loginWithKaKao}> kakao check</button>
        <KakaoLogin token={KakaoAuthKey} onSuccess={(res) => {console.log(res)}} onFail={(err) => {console.log(err)}} onLogout={console.info} render={({ onClick }) => {

            return (
                <a
                href="/"
                onClick={(e) => {
                    e.preventDefault();
                    onClick();
                }}
                >
                카카오로 로그인하기
                </a>
            );
        }} />        
        <h3>아직 계정이 없으신가요?</h3> <Link to="/sign-up">회원가입하기</Link>
    </div>
    );
};

export default SignIn;
