const useConfirm = (msg= "", onConfirm, onCancel) => {
	if (onCancel && typeof onCancel !== "function"){
		return;
	}
	if(onConfirm && typeof onConfirm !== "function"){
		return;
	}
	const confirmAction = () => {
		if (useConfirm(msg)){
			onConfirm();
		} else {
			onCancel();
		}
	}
	return confirmAction;
};

export default useConfirm;
