import React, {useEffect, useState} from "react";
import { Link, useHistory } from "react-router-dom";
import "components/styles/Home.css"
import firebase from "global/fbase";

const Home  = ({ isLoggedIn }) => {
    //prop 바뀌면 리렌더링 isLoggedIn state 바뀜

    const history = useHistory();
    //login 안 된 경우, 로그인 필요합니다 알러트 띄우기
    const onClick = (event) => {
        event.preventDefault();
        if (isLoggedIn){
            history.push('/add-road-info');
        } else {
            window.alert("산책로 정보를 추가하려면 로그인이 필요합니다.");
            history.push('sign-in');
        }
    };
    
    const [arr, setArr]= useState([]);

    const getWalkRoadInfo = async () => {
            const res = await firebase.firestore().collection('WalkRoad').get()
    
            res.forEach((doc) => {
                const arrObj = {
                    ...doc.data(),
                    id: doc.id,
                };
                setArr((prev) => [arrObj, ...prev]);
            });
    };
     
    // 화면 켜질때 딱 한번만 array에 담는걸 어떻게 할까.. 
    useEffect(() => {
        getWalkRoadInfo();
    }, [])

    // const height = window.screen.availHeight - '61px';

 
    return (
        <>
            <div id="first-container" style={{
                display: 'flex',
                flexDirection:'row',
                height: `${window.innerHeight - 111}px`,
                flexWrap:'wrap',
                width:window.innerWidth,
                backgroundColor:'yellow',
                justifyContent: 'flex-start',
                alignItems: 'center'
            }}>
                <div style={{
                    display:'inline-flex',
                    flexDirection:"column",
                    flexWrap:'wrap',
                    height: `${window.innerHeight / 2}px`,
                    justifyContent:'center',
                    marginRight:'13%',
                    marginLeft: "13%",
                }}>
                    <h1 style={{
                        marginTop:'0',
                        fontSize:'55px',
                        whiteSpace: 'pre'
                    }}>
                        {
                        `시각장애인의 
눈과 발이 되어주세요.`}
                    </h1>
                    <h3 style={{
                        marginTop:'0',
                        fontSize:'20px',
                        whiteSpace: 'pre',
                        fontWeight:'500'
                    }}>
                        {`
산책로 정보를 입력하시면, 
여가활동이 어려운 시각장애인에게
다양하고 편안한 산책 경험을 제공할 수 있습니다.`}
                    </h3>
                </div>

                <div className="linkBox" style={{
                    display:'inline-block'
                }}>
                    <Link to="/add-road-info" onClick={onClick} style={{
                        display: 'inline-block',
                        color: 'black',
                        textDecoration: 'none',
                        border: '4px solid black',
                        borderRadius: '20px',
                        textAlign: 'center',
                        padding: '0px 68px',
                        width:'53%'
                    }}>
                        <h4 style={{
                            fontSize: '18px',
                            whiteSpace:'pre',
                            padding:'112% 0',
                            marginBlockEnd:'0',
                            lineHeight:'300%'
                        }}>
{`이곳을 눌러
정보를 추가해주세요`}
                        </h4>
                    </Link>
                </div>
                
                <div style={{
                    display:'flex',
                    height:'50px',
                    width:window.innerWidth,
                    backgroundColor:'white'
                }}></div>

            </div>
        </>
    );
};

export default Home;
