import React from "react";
import {
    useRecoilValue,
  } from 'recoil';
import SupportInfraItemCreator from 'components/molecules/ForAddRoad/3SuppportInfra/SupportInfraItemCreator';
import SupportInfraItem from 'components/molecules/ForAddRoad/3SuppportInfra/SupportInfraItem';
import SupportInfraListState from 'recoilStates/SupportInfraListState';
import Aroad from 'components/molecules/ForAddRoad/1Roadname/Aroad';

import BroadEnv from 'components/molecules/ForAddRoad/2RoadEnv/BroadEnv';


import WarningInput from 'components/molecules/ForAddRoad/7WarnFeature/WarningInput';
import FeaturesInput from 'components/molecules/ForAddRoad/7WarnFeature/FeaturesInput';

import InfraListState from 'recoilStates/InfraListState';
import InfraItem from 'components/molecules/ForAddRoad/4RestPlace/InfraItem';
import InfraItemCreator from 'components/molecules/ForAddRoad/4RestPlace/InfraItemCreator';

import AroundEnvListState from 'recoilStates/AroundEnvListState';
import AroundEnvItem from 'components/molecules/ForAddRoad/6AroundEnv/AroundEnvItem';

import SearchAroundEnv from 'components/molecules/ForAddRoad/6AroundEnv/SearchAroundEnv';
import EnvSearchState from 'recoilStates/searched/EnvSearchState';
import EnvSearchResults from 'components/molecules/ForAddRoad/6AroundEnv/EnvSearchResult';
import PeopleInput from 'components/molecules/ForAddRoad/5FloatingPop/PeopleInput';
import ReactPageScroller from 'react-page-scroller';

const AddRoadForm = () => {
    const supportInfraList = useRecoilValue(SupportInfraListState);
    const infraList = useRecoilValue(InfraListState);
    const aroundList = useRecoilValue(AroundEnvListState);

    const envSearchResults = useRecoilValue(EnvSearchState);


    return (
      <>
        < >
          <Aroad />
          <BroadEnv />

          <h4>보조시설 추가</h4>
          <SupportInfraItemCreator />
          {supportInfraList.map((supportInfraItem) => (
            <SupportInfraItem key={supportInfraItem.id} item={supportInfraItem} />
          ))}


          <h4>시설 추가</h4>
          <InfraItemCreator />
          {infraList.map((infraItem) => (
            <InfraItem key={infraItem.id} item={infraItem} />
          ))}

          <h4>주변환경 추가</h4>
          <SearchAroundEnv placeholder="음식점 이름 등을 입력하세요" />
          <div id="aroundenv">
            {envSearchResults.map((data) => (
              <EnvSearchResults key={data.id} data={data} />
            ))}
          </div>

          <div id="aroundEnvList">
            {aroundList.map((aroundEnvItem) => (
              <AroundEnvItem key={aroundEnvItem.id} item={aroundEnvItem} />
            ))}
          </div>


          <h4>걸을 때 주의사항</h4>
          <WarningInput placeholder="걸을 때 주의사항을 적어주세요" />

          <h4>산책로 특징</h4>
          <FeaturesInput placeholder="산책로의 특징을 적어주세요" />

          <h4>유동 인구</h4>
          <PeopleInput placeholder="유동인구 많은 정도 적어주세요" />
        </>
      </>
    );
};

export default AddRoadForm;
