import {
    useSetRecoilState,
  } from 'recoil';
import React, {useState} from 'react';
import todoListState from 'recoilStates/todoListState';
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';


let id = 0;

function getId() {
   return id++;
}

const TodoItemCreator = () => {
    const [latitude, setLatitude] = useState('');
    const [longitude, setLongitude] = useState('');
    const [state, setState] = useState(''); // 이 친구도 리코일로 바꿔야 할까..?
    const options = [
        "음성유도기", "유도블럭", "점자 표지판", "안전펜스"
    ];
    const onSelect = (event) => {
        setState(event.value);
    };
    const setTodoList = useSetRecoilState(todoListState);

    const addItem = () => {
      setTodoList((oldTodoList) => [
        ...oldTodoList,
        {
          id: getId(),
          text: `${latitude}, ${longitude}`,
        },
      ]);
      setLatitude('');
      setLongitude('');
    };

    const onChange = ({target: {name, value}}) => {
      if (name === "lat"){ 
        setLatitude(value);
      } else if (name === "lng") {
        setLongitude(value);
      }
    };

    return (
      <div>
        <Dropdown options={options} onChange={onSelect} value={state} placeholder="Select an option" />
        <input name="lat" type="text" value={latitude} onChange={onChange} />
        <input name="lng" type="text" value={longitude} onChange={onChange} />
        <button onClick={addItem}>Add</button>
      </div>
    );
}


export default TodoItemCreator;
