import React, {useEffect,  useState} from "react";
import { Link, useHistory } from "react-router-dom";
import "components/styles/Home.css"
import firebase from "global/fbase";
import useScroll from 'hooks/useScroll';
import useH1FadeIn from 'hooks/useH1FadeIn';
import useH3FadeIn from 'hooks/useH3FadeIn';


const Home  = ({ isLoggedIn }) => {
    //prop 바뀌면 리렌더링 isLoggedIn state 바뀜

    const history = useHistory();
    //login 안 된 경우, 로그인 필요합니다 알러트 띄우기
    const onRoadInfoClick = (event) => {
        event.preventDefault();
        if (isLoggedIn){
            history.push('/add-road-info');
        } else {
            window.alert("산책로 정보를 추가하려면 로그인이 필요합니다.");
            history.push('sign-in');
        }
    };

    const onSoundBtnClick = (event) => {
        // event.preventDefault();
        if (event.target.id === 'first-sound-btn') {
            document.getElementById('first-h4').style.opacity = 0;
            document.getElementById('second-test-box').style.display = 'inline-flex';
            document.getElementById('second-sound-btn').style.display = 'inline-flex';
            document.getElementById('second-h4').style.display = 'inline-flex';
        } 
    }
    
    // const [arr, setArr]= useState([]);

    // const getWalkRoadInfo = async () => {
    //         const res = await firebase.firestore().collection('WalkRoad').get()
    
    //         res.forEach((doc) => {
    //             const arrObj = {
    //                 ...doc.data(),
    //                 id: doc.id,
    //             };
    //             setArr((prev) => [arrObj, ...prev]);
    //         });
    // };
     
    // // 화면 켜질때 딱 한번만 array에 담는걸 어떻게 할까.. 
    // useEffect(() => {
    //     getWalkRoadInfo();
    // }, [])

    const {y} = useScroll();


    const h1FadeIn = useH1FadeIn();
    const h3FadeIn = useH3FadeIn();
    

 
    return (
        <>
            <div id="first-container" style={{
                display: 'flex',
                flexDirection:'row',
                height: `${window.innerHeight - 126}px`,
                flexWrap:'wrap',
                width:window.innerWidth,
                backgroundColor:'white',
                justifyContent: 'flex-start',
                alignItems: 'center'
            }}>
                <div style={{
                    display:'inline-flex',
                    flexDirection:"column",
                    flexWrap:'wrap',
                    height: `${window.innerHeight / 2}px`,
                    justifyContent:'center',
                    marginRight:'15%',
                    marginLeft: "15%",
                }}>
                    <h1 {...h1FadeIn}>
                        {
                        `시각장애인의 
눈과 발이 되어주세요.`}
                    </h1>
                    <h3 {...h3FadeIn}>
                        {`
산책로 정보를 입력하시면, 
여가활동이 어려운 시각장애인에게
다양하고 편안한 산책 경험을 제공할 수 있습니다.`}
                    </h3>
                </div>

                <div className="linkBox" style={{
                    display:'inline-block'
                }}>
                    <Link to="/add-road-info" onClick={onRoadInfoClick} style={{
                        display: 'inline-block',
                        color: 'black',
                        textDecoration: 'none',
                        border: '3px solid black',
                        borderRadius: '20px',
                        textAlign: 'center',
                        padding: '0px 80px',
                        width:'53%'
                    }}>
                        <h4 style={{
                            fontSize: '18px',
                            whiteSpace:'pre',
                            padding:'112% 0',
                            marginBlockEnd:'0',
                            lineHeight:'300%'
                        }}>
{`이곳을 눌러
정보를 추가해주세요`}
                        </h4>
                    </Link>
                </div>
            </div>
            <div style={{
                display:'flex',
                height:'65px',
                flexDirection:'column',
                width:window.innerWidth,
                backgroundColor:'white',
                justifyContent:'flex-start',
                alignItems:'center'
            }}>
                <h5 style={{
                    fontSize:'15px',
                    margin:'0'
                }}>스크롤을 내려 워크위드를 체험해보세요</h5>
                <img src={require('images/down-arrow.png').default} alt='스크롤을 내려보세요' style={{
                    paddingTop:'3px',
                    paddingBottom: '0',
                    height:'30px',
                    width:'65px'
                }} />
            </div>
            {y > 1 ? <div className="test">
                <div className="first-test-box">
                    <h4 id='first-h4' style={{
                        flexWrap:'wrap',
                        whiteSpace:'pre',
                        marginBlockEnd:'0',
                        color:'white',
                        marginRight:'40px'
                    }}>
{`이곳을 눌러 
소리를 들어보세요 ->`
}</h4>    
                    <button id="first-sound-btn" onClick={onSoundBtnClick}>
{/* 사운드 버튼 부분, */}
                    </button>
                </div> 
                <div id="second-test-box">
                    <h4 id='second-h4' style={{
                        flexWrap:'wrap',
                        whiteSpace:'pre',
                        marginBlockEnd:'0',
                        color:'white',
                        marginRight:'40px',
                        display:'none',
                        animationName:'myopacity',
                        animationDelay:'0.5s'
                    }}>
{`이곳을 눌러 
소리를 들어보세요 ->`
}</h4>    
                    <button id="second-sound-btn" onClick={onSoundBtnClick}>
{/* 사운드 버튼 부분, */}
                    </button>
                </div> 
            </div>
            : <></>
            }
            <div style={{
                display: 'flex',
                width:window.innerWidth,
                height:'600px'
            }}></div>
        
        </>
    );
};

export default Home;
