import React, { useState } from "react";
import {
    useRecoilValue,
  } from 'recoil';
import TodoItemCreator from 'functions/TodoItemCreator';
import TodoItem from 'components/molecules/TodoItem';
import todoListState from 'recoilStates/todoListState';
import InputAddress from 'components/molecules/InputAddress';

const AddRoad = () => {
    const todoList = useRecoilValue(todoListState);
    //정보등록 onclick으로 firebase.firestore 한번에 넣기
    const [roadName, setRoadName] = useState('');

    const onChange = (event) => {
      const {
          target: {
              name, value
          }
      } = event;
      if (name === "road-name") {
          setRoadName(value);
      }
  }; 

    return (
      <>
        <h3>AddRoad Page</h3>
        <input name="road-name" onChange={onChange} value={roadName} placeholder="산책로 이름을 입력하세요" required/>
        <InputAddress />
        <TodoItemCreator />
        {todoList.map((todoItem) => (
          <TodoItem key={todoItem.id} item={todoItem} />
        ))}
      </>
    );
};

export default AddRoad;
